import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_native_splash/flutter_native_splash.dart';import 'package:music_app/bloc/cubits/audio/audio_cubit.dart';import 'package:music_app/helper/AppColor.dart';import 'package:music_app/helper/AppFont.dart';import 'package:music_app/helper/AppRoute.dart';import 'package:music_app/ui/pages/player/JustAudioPage.dart';import 'ui/bottom_navigation_bar/BottomNavBar.dart';import 'ui/pages/home/HomePage.dart';import 'ui/pages/library/LibraryPage.dart';void main() {  WidgetsBinding widgetsBinding = WidgetsFlutterBinding.ensureInitialized();  FlutterNativeSplash.preserve(widgetsBinding: widgetsBinding);  SystemChrome.setEnabledSystemUIMode(SystemUiMode.edgeToEdge);  SystemChrome.setSystemUIOverlayStyle(    const SystemUiOverlayStyle(      statusBarColor: Colors.transparent,      statusBarIconBrightness: Brightness.dark,      systemNavigationBarColor: Colors.transparent,      systemNavigationBarContrastEnforced: false,      systemNavigationBarDividerColor: Colors.transparent,      systemNavigationBarIconBrightness: Brightness.dark,    ),  );  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => AudioCubit(),      child: MaterialApp(        title: 'Flutter Demo',        debugShowCheckedModeBanner: false,        theme: ThemeData(          colorScheme: ColorScheme.fromSeed(            seedColor: AppColor.primary,            background: Colors.white,            secondary: Colors.white,          ),          useMaterial3: true,          fontFamily: AppFont.inter,          canvasColor: Colors.white,        ),        routes: _routes(),        initialRoute: AppRoute.initial,      ),    );  }  Map<String, WidgetBuilder> _routes() => <String, WidgetBuilder>{        AppRoute.initial: (context) => const BottomNavBarPage(),        AppRoute.home: (context) => const HomePage(),        AppRoute.library: (context) => const LibraryPage(),        AppRoute.justAudio: (context) => const JustAudioPage(),      };}